package ex2_animal;

public class Sneak extends Animal {
	
	int leg = 0;
	String sense = "감각이 발달했다";
	
	public Sneak() {
		// TODO Auto-generated constructor stub
	}

	public int getLeg() {
		return leg;
	}

	public void setLeg(int leg) {
		this.leg = leg;
	}

	public String getSense() {
		return sense;
	}

	public void setSense(String sense) {
		this.sense = sense;
	}
	
	
}
